import React, { useState } from 'react';
import { FcGoogle } from 'react-icons/fc';
import './Login.css';

function Login() {
  const [isSignIn, setIsSignIn] = useState(true);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [validationCode, setValidationCode] = useState('');
  const [isCodeSent, setIsCodeSent] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignIn) {
      console.log('Signing in', username, password);
    } else {
      console.log('Registering', username, email, password, validationCode);
    }
  };

  const handleGoogleLogin = () => {
    console.log('Logging in with Google');
  };

  const handleSendCode = () => {
    if (email) {
      // Here you would typically call an API to send the validation code
      console.log('Sending validation code to', email);
      setIsCodeSent(true);
      // You might want to start a timer here to allow resending after a certain period
    } else {
      alert('Please enter your email first');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>{isSignIn ? 'Sign In' : 'Register'}</h2>
        <div className="toggle-buttons">
          <button 
            className={isSignIn ? 'active' : ''} 
            onClick={() => setIsSignIn(true)}
          >
            Sign In
          </button>
          <button 
            className={!isSignIn ? 'active' : ''} 
            onClick={() => setIsSignIn(false)}
          >
            Register
          </button>
        </div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          {!isSignIn && (
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          )}
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {!isSignIn && (
            <div className="validation-code-container">
              <input
                type="text"
                placeholder="Validation Code"
                value={validationCode}
                onChange={(e) => setValidationCode(e.target.value)}
                required
              />
              <button 
                type="button" 
                onClick={handleSendCode}
                disabled={isCodeSent}
                className="send-code-btn"
              >
                {isCodeSent ? 'Sent' : 'Send Code'}
              </button>
            </div>
          )}
          <button type="submit" className="submit-btn">
            {isSignIn ? 'Sign In' : 'Register'}
          </button>
        </form>
        {isSignIn && (
          <>
            <div className="divider">OR</div>
            <button onClick={handleGoogleLogin} className="google-btn">
              <FcGoogle /> Sign in with Google
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default Login;
